mapper.select:
		select M.*, P.upload_name, P.save_name from
    	menu M inner join menu_photo P on M.menu_no=P.menu_no
		where M.menu_no = #{menu_no}

select M.*, B.branch_no, B.menu_status, P.upload_name, P.save_name from menu M inner join branch_menu B on B.menu_no=M.menu_no inner join menu_photo P on M.menu_no=P.menu_no where B.branch_no=#{branch_no} and not B.menu_status='3'
and M.category = #{category}
order by M.menu_name asc
